import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "../styles/Home.css";
import styled from "styled-components";

function Home() {
  // Load likes from localStorage or default to 0
  const [likes, setLikes] = useState(() => {
    const savedLikes = JSON.parse(localStorage.getItem("likes"));
    return savedLikes || { like1: 0, like2: 0, like3: 0 };
  });

  // Save likes to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem("likes", JSON.stringify(likes));
  }, [likes]);

  const liked1 = () => {
    setLikes((prevLikes) => ({ ...prevLikes, like1: prevLikes.like1 + 1 }));
  };

  const liked2 = () => {
    setLikes((prevLikes) => ({ ...prevLikes, like2: prevLikes.like2 + 1 }));
  };

  const liked3 = () => {
    setLikes((prevLikes) => ({ ...prevLikes, like3: prevLikes.like3 + 1 }));
  };

  return (
    <>
      <h1 style={{ textAlign: "center" }}>MY PROJECTS</h1>
      <div>
        <div className="col-md-4">
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">Callback Task</h5>
              <button
                onClick={liked1}
                style={{ border: "none", background: "transparent" }}
              >
                üëç
                <p>{likes.like1}</p>
              </button>
              <Link to="/calling" className="btn btn-primary">
                View Project
              </Link>
            </div>
          </div>
        </div>
      </div>
      <div className="col-md-4">
        <div className="card">
          <div className="card-body">
            <h5 className="card-title">Table Generation</h5>
            <button
              onClick={liked2}
              style={{ border: "none", background: "transparent" }}
            >
              üëç
              <p>{likes.like2}</p>
            </button>
            <Link to="/Table" className="btn btn-primary">
              View Project
            </Link>
          </div>
        </div>
      </div>
      <div className="col-md-4">
        <div className="card">
          <div className="card-body">
            <h5 className="card-title">Redux</h5>
            <button
              onClick={liked3}
              style={{ border: "none", background: "transparent" }}
            >
              üëç
              <p>{likes.like3}</p>
            </button>
            <Link to="/counter-redux" className="btn btn-primary">
              View Project
            </Link>
          </div>
        </div>
      </div>
    </>
  );
}

export default Home;
