import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { increment, addCart } from './counterSlice';
import styles from './Counter.module.css';
import img from "../../Images/card.jpg";

export function Counter() {
  const carts = [{
    id: 1,
    title: "Product 1",
    image: img,
    link: "/counter-redux"
  },
  {
    id: 2,
    title: "Product 2",
    image: img,
    link: "/counter-redux"
  },
  {
    id: 3,
    title: "Product 3",
    image: img,
    link: "/counter-redux"
  },
  {
    id: 4,
    title: "Product 4",
    image: img,
    link: "/counter-redux"
  },
  {
    id: 5,
    title: "Product 5",
    image: img,
    link: "/counter-redux"
  },
  {
    id: 6,
    title: "Product 6",
    image: img,
    link: "/counter-redux"
  },
  {
    id: 7,
    title: "Product 7",
    image: img,
    link: "/counter-redux"
  },
  {
    id: 8,
    title: "Product 8",
    image: img,
    link: "/counter-redux"
  },
  {
    id: 9,
    title: "Product 9",
    image: img,
    link: "/counter-redux"
  },
  {
    id: 10,
    title: "Product 10",
    image: img,
    link: "/counter-redux"
  },
  {
    id: 11,
    title: "Product 11",
    image: img,
    link: "/counter-redux"
  },
  {
    id: 12,
    title: "Product 12",
    image: img,
    link: "/counter-redux"
  },
  {
    id: 13,
    title: "Product 13",
    image: img,
    link: "/counter-redux"
  },
  {
    id: 14,
    title: "Product 14",
    image: img,
    link: "/counter-redux"
  },
  {
    id: 15,
    title: "Product 15",
    image: img,
    link: "/counter-redux"
  },
  {
    id: 16,
    title: "Product 16",
    image: img,
    link: "/counter-redux"
  },
  {
    id: 17,
    title: "Product 17",
    image: img,
    link: "/counter-redux"
  },
  {
    id: 18,
    title: "Product 18",
    image: img,}
];
  
  const count = useSelector((state) => state.counter.value);
  const addedCarts = useSelector((state) => state.counter.carts);
  const showCart = useSelector((state) => state.counter.showCart);
  const dispatch = useDispatch();

  const handleAddToCart = (cart) => {
    dispatch(increment());
    dispatch(addCart(cart));
  };
  const addedcart = carts.filter((cart) => 
  cart.id === addCart.id);

  return (
    <div className={styles.counterContainer}>
      <div className="card">
        {ca.map((cart) => (
          <div key={cart.id}>
            <img className="card-img-top" src={cart.image} alt={cart.title} />
            <div className="card-body">
              <h4 className="card-title" style={{ color: "#000102ac" }}>{cart.title}</h4>
              <p className="card-text">See the product and choose what you need!</p>
              <button
                className={styles.counterButton}
                aria-label="Add to cart"
                onClick={() => handleAddToCart(cart)}
              >
                Add to cart
              </button>
              <span className={styles.counterValue}>{count}</span>
            </div>
          </div>
        ))}
      </div>

      {showCart && (
        <div className={styles.addedCarts}>
          <h3>Added Carts</h3>
          <ul>
            {addedCarts.map((cart, index) => (
              <li key={index}>{cart.title}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default Counter;
