import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { increment } from './counterSlice';
import styles from './Counter.module.css';
import img from "../../Images/card.jpg"
export function Counter() {

 const  carts=[{
    id:1,
    title:"Product 1",
    image:img,
    link:"/counter-redux"
   }]
  const count = useSelector((state) => state.counter.value);
  const dispatch = useDispatch();



  return (
    <div className={styles.counterContainer}>
      <div class="card">
        {carts.map((cart)=>(
        <><img class="card-img-top" src={cart.image} alt="Title" /><div class="card-body">

            <h4 class="card-title" style={{ color: "#000102ac" }}>{cart.title}</h4>
            <p class="card-text">See the product and choose what u need!!1</p>
            <button
              className={styles.counterButton}
              aria-label="Increment value"
              onClick={() => dispatch(increment())}
            >
              Add to cart
            </button>
            <span className={styles.counterValue}>{count}</span>
          </div></>))
        }
       
      </div>
    
      
    </div>
  );
}

export default Counter;
