import React, { useState } from 'react';
import Navbar1 from './Navbar';
import axios from 'axios';

const Attendance = () => {
    const [name, setName] = useState('');
    const [rollNo, setRollNo] = useState('');
    const [attendance, setAttendance] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:5000/attendance', {
                name,
                rollNo,
                attendance
            });
            
            // Reset form fields after successful submission
            setName('');
            setRollNo('');
            setAttendance('');
            alert('Attendance submitted successfully');
        } catch (error) {
            console.error('Error submitting attendance:', error);
            alert('Failed to submit attendance');
        }
    };

    return (
        <>
            <Navbar1 />
            {/* marking attendence on todays note */}
            <div className="container">
                <h1>Attendance</h1>
                <form onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="name">Name</label>
                        <input type="text" className="form-control" id="name" value={name} onChange={(e) => setName(e.target.value)} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="rollno">Roll No</label>
                        <input type="text" className="form-control" id="rollno" value={rollNo} onChange={(e) => setRollNo(e.target.value)} />
                    </div>
                    <div className="form-group">
                         <label>Attendance</label> <br />
                         <input type="radio" id="present" name="attendance" value="present" onChange={(e) => setAttendance(e.target.value)} />
                        <label htmlFor="present">Present</label> &nbsp;
                        <input type="radio" id="absent" name="attendance" value="absent" onChange={(e) => setAttendance(e.target.value)} />
                        <label htmlFor="absent">Absent</label> <br /> 

                    </div>
                    <br />
                    <button type="submit" className="btn btn-primary">Submit</button>
                </form>
            </div>
        </>
    );
};

export default Attendance;
