import React, { useState } from 'react';
import Callback from './components/Callback';
import "./App.css";

function App() {
  const [UIcolor, setUIcolor] = useState(null);

  const getColor = (color) => {
    setUIcolor(color);
  };

  return (
    <div className="App">
      <div className='color-picker' style={{background:`${UIcolor}`}}>
        {/* Content of your color picker component */}
        <input type="text" id="colorInput" name="color" />
        <button onClick={() => getColor("color")}>Set Color</button> {/* Ensure correct usage */}
      </div>
      <Callback getColor={getColor} /> {/* Passing getColor function as prop */}
    </div>
  );
}

export default App;
