import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { increment, addCart } from './counterSlice';
import styles from './Counter.module.css';
import img from "../../Images/card.jpg";

export function Counter() {
  const productList = [
    { id: 1, title: "Product 1", image: img, link: "/counter-redux" },
    { id: 2, title: "Product 2", image: img, link: "/counter-redux" },
    // Add more products as needed
  ];

  const count = useSelector((state) => state.counter.value);
  const addedCarts = useSelector((state) => state.counter.carts);
  const showCart = useSelector((state) => state.counter.showCart);
  const dispatch = useDispatch();

  const handleAddToCart = (product) => {
    dispatch(increment());
    dispatch(addCart(product));
  };

  return (
    <div className={styles.counterContainer}>
      {!showCart ? (
        <div className="card">
          {productList.map((product) => (
            <div key={product.id}>
              <img className="card-img-top" src={product.image} alt={product.title} />
              <div className="card-body">
                <h4 className="card-title" style={{ color: "#000102ac" }}>{product.title}</h4>
                <p className="card-text">See the product and choose what you need!</p>
                <button
                  className={styles.counterButton}
                  aria-label="Add to cart"
                  onClick={() => handleAddToCart(product)}
                >
                  Add to cart
                </button>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className={styles.addedCarts}>
          <h3>Added Carts</h3>
          {addedCarts.map((cart) => (
            <div key={cart.id} className={styles.cartItem}>
              <img src={cart.image} alt={cart.title}width={"100%"} />
              <p>{cart.title}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Counter;
